import styled from 'styled-components';
import "./Home.css"


const Container = styled.div`
display: flex;
height: 100vh;
background-color:black;
margin-top: 30px;
@media(max) {
    
}


`
const Part1 = styled.div`
flex: 1;
display: flex;
flex-direction: column;

`
const Part2 = styled.div`
flex: 1;
display: flex;
justify-content: center;
align-items: center;

`
const Title = styled.div`
margin-left: 5%;
color: #F3015D;
`
const Text = styled.div`
margin-left: 10%;
color: white;
`


 export const Home = ()=> {
    return(
        <Container>
            <Part1>
                <div className='body-img'>
                </div>
                <div className='body-text'>
                <Title><h1>Welcome</h1></Title>
                <Text><h3>Sou desenvolvedor focado em desenvolvimento front-end.Tenho dedicação e esforço como programador web.E acredito na tecnologia como uma grande evolução para a humanidade em que vivemos.</h3></Text>
                </div>
            </Part1>
            <Part2>
                <div className='body-animation'></div>
            </Part2>
        </Container>
    )
}
